CREATE CACHED TABLE groups (id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, group_number INTEGER NOT NULL, faculty_name VARCHAR(255) NOT NULL);

CREATE CACHED TABLE students (id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, name VARCHAR(60) NOT NULL, last_name VARCHAR(60) NOT NULL, middle_name VARCHAR(60) NOT NULL, birth_date DATE NOT NULL, group_id BIGINT, CONSTRAINT fk_student_group FOREIGN KEY (group_id) REFERENCES groups(id));
